int main ( ) { int n , a , b , x , y ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d%d" , & x , & y ) ; a = x ; b = y ; while ( a != b ) { if ( a > b ) a = a - b ; else b = b - a ; } printf ( "%d\n" , a ) ; a = x ; b = y ; while ( a != b ) { if ( a < b ) a = a + x ; else b = b + y ; } printf ( "%d" , a ) ; } return 0 ; }