int main ( int argc , char * argv [ ] ) { int * results ; int * sums = malloc ( MAX_SIZE * sizeof ( int ) ) ; int * prev_sums = malloc ( MAX_SIZE * sizeof ( int ) ) ; int round , rounds ; int line , width ; int i , tmp , max ; scanf ( "%d" , & rounds ) ; results = calloc ( rounds , sizeof ( int ) ) ; for ( round = 0 ; round < rounds ; ++ round ) { scanf ( "%d" , & width ) ; for ( i = 0 ; i < width ; ++ i ) sums [ i ] = prev_sums [ i ] = 0 ; for ( line = 0 ; line < width ; ++ line ) { for ( i = 0 ; i < ( line + 1 ) ; ++ i ) { scanf ( "%d" , & tmp ) ; if ( i == 0 ) { sums [ i ] = prev_sums [ i ] + tmp ; } else { sums [ i ] = prev_sums [ i ] > prev_sums [ i - 1 ] ? prev_sums [ i ] + tmp : prev_sums [ i - 1 ] + tmp ; } } for ( i = 0 ; i < ( line + 1 ) ; ++ i ) prev_sums [ i ] = sums [ i ] ; } for ( max = i = 0 ; i < line + 1 ; ++ i ) { if ( max < sums [ i ] ) { max = sums [ i ] ; } } printf ( "%d\n" , max ) ; } free ( prev_sums ) ; free ( sums ) ; free ( results ) ; return 0 ; }