int main ( void ) { int t ; scanf ( "%i" , & t ) ; for ( int z = 0 ; z < t ; z ++ ) { int a , b ; unsigned long long int lcm , gcd ; scanf ( "%i%i" , & a , & b ) ; for ( unsigned long long int i = 1 ; i <= a * b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) gcd = i ; if ( i % a == 0 && i % b == 0 ) { lcm = i ; break ; } } printf ( "%llu %llu\n" , gcd , lcm ) ; } return 0 ; }