int main ( ) { int i , t , num1 , num2 , remainder , denominator , numerator , gcd , lcm ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d%d" , & num1 , & num2 ) ; numerator = num1 ; denominator = num2 ; remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd = denominator ; lcm = ( num1 * num2 ) / gcd ; printf ( "%d %d\n" , gcd , lcm ) ; } return 0 ; }