int main ( ) { int a [ 2 ] ; int b , i = 0 , j , d , c ; scanf ( "%d" , & b ) ; while ( i < b ) { scanf ( "%d" , & a [ 0 ] ) ; scanf ( "%d" , & a [ 1 ] ) ; j = 1 ; if ( a [ 0 ] > a [ 1 ] ) { d = a [ 0 ] , c = a [ 1 ] ; } else { d = a [ 1 ] , c = a [ 0 ] ; } while ( j != 0 ) { j = d % c ; d = c ; c = j ; } printf ( "%d\n" , d ) ; printf ( "%d\n" , ( ( a [ 0 ] * a [ 1 ] ) / d ) ) ; i = i + 1 ; } return 0 ; }