int main ( ) { int number1 , number2 , test , i , x , a , b , c ; int gcd [ 1000 ] ; int lcm [ 1000 ] ; scanf ( " %d" , & test ) ; for ( i = 0 ; i < test ; i ++ ) { scanf ( " %d %d" , & number1 , & number2 ) ; a = number1 ; b = number2 ; while ( b != 0 ) { x = b ; b = a % x ; a = x ; } gcd [ i ] = a ; c = number1 / gcd [ i ] ; lcm [ i ] = number2 * c ; } for ( i = 0 ; i < test ; i ++ ) { printf ( "%d %d\n" , gcd [ i ] , lcm [ i ] ) ; } return 0 ; }