int main ( ) { long int a [ 100 ] , b [ 100 ] , k ; long int i , j , n , max , min ; scanf ( "%ld" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) scanf ( "%ld%ld" , & a [ i ] , & b [ i ] ) ; for ( i = 1 ; i <= n ; i ++ ) { if ( a [ i ] > b [ i ] ) { max = a [ i ] ; min = b [ i ] ; } else { max = b [ i ] ; min = a [ i ] ; } if ( max % min == 0 ) printf ( "%ld " , min ) ; else { for ( j = min - 1 ; j > 1 ; j -- ) { if ( ( max % j == 0 ) && ( min % j == 0 ) ) break ; } printf ( "%ld " , j ) ; } if ( max % min == 0 ) printf ( "%ld" , min ) ; else { k = max + 1 ; do { k = k + 1 ; } while ( ( k % max != 0 ) || ( k % min != 0 ) ) ; printf ( "%ld" , k ) ; } printf ( "\n" ) ; } return 0 ; }