int main ( ) { long long int t , i , a , x , b , min , max , j , gcd , lcm ; scanf ( "%lld" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; min = a < b ? a : b ; gcd = 1 ; for ( j = 2 ; j <= min ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { gcd = j ; } } max = a > b ? a : b ; j = max ; x = a * b ; while ( j <= x ) { if ( j % a == 0 && j % b == 0 ) { lcm = j ; break ; } j = j + max ; } printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }