int main ( ) { int t , sum , i , j , n , max ; scanf ( "%d" , & t ) ; while ( t -- ) { sum = 0 ; scanf ( "%d" , & n ) ; int a [ n ] [ n ] ; for ( i = 0 ; i <= n - 1 ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } for ( i = 1 ; i <= n - 1 ; i ++ ) { max = a [ i ] [ 0 ] ; for ( j = 0 ; j <= i ; j ++ ) { if ( a [ i ] [ j ] > max ) max = a [ i ] [ j ] ; } sum += max ; } printf ( "%d\n" , sum ) ; } }