main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; scanf ( "%d %d" , & a , & b ) ; printf ( "%d %d\n" , gcd ( a , b ) , lcm ( a , b ) ) ; } } int gcd ( int x , int y ) { if ( x == 1 && y == 1 ) return ( 1 ) ; if ( x == y ) return ( x ) ; if ( x > y ) return gcd ( x - y , y ) ; return gcd ( x , y - x ) ; } int lcm ( int x , int y ) { int l ; l = ( x * y ) / gcd ( x , y ) ; return ( l ) ; }