int main ( ) { int t , i , c , g , sum = 0 ; scanf ( "%d" , & t ) ; while ( t > 0 ) { c = 0 ; i = 0 ; sum = 0 ; g = 0 ; t -- ; scanf ( "%d" , & i ) ; int a [ i ] ; g = i ; for ( c = 0 ; c < i ; c ++ ) { scanf ( "%d" , & a [ c ] ) ; } for ( c = 0 ; c < i - 1 ; c ++ ) { if ( a [ 0 ] < a [ 1 ] ) { a [ 1 ] = a [ g - 1 ] ; sum = sum + a [ 0 ] ; g -- ; } else { sum = sum + a [ 1 ] ; a [ 0 ] = a [ g - 1 ] ; g -- ; } } printf ( "%d" , sum ) ; if ( t > 0 ) { printf ( "\n" ) ; } } return 0 ; }