main ( ) { int a ; int i , j , n ; scanf ( "%d" , & a ) ; int num = a ; int r [ a ] ; while ( a > 0 ) { int sum = 0 ; int b [ 100 ] [ 100 ] ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { int m = 0 ; for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & b [ i ] [ j ] ) ; if ( b [ i ] [ j ] > m ) m = b [ i ] [ j ] ; } sum = sum + m ; } r [ a - 1 ] = sum ; a -- ; } for ( i = num - 1 ; i >= 0 ; i -- ) printf ( "%d\n" , r [ i ] ) ; }