int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , j , k , temp ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( j = 0 ; j < n ; j ++ ) { scanf ( "%d" , & a [ j ] ) ; } for ( j = 0 ; j < n - 1 ; j ++ ) { for ( k = 0 ; k < ( n - j ) - 1 ; k ++ ) { if ( a [ k ] > a [ k + 1 ] ) { temp = a [ k ] ; a [ k ] = a [ k + 1 ] ; a [ k + 1 ] = temp ; } } } int cost = ( n - 1 ) * a [ 0 ] ; printf ( "%d\n" , cost ) ; } return 0 ; }