long int gcd ( long int a , int b ) { if ( a == 0 || b == 0 ) return 0 ; else if ( a == b ) return a ; else if ( a > b ) return gcd ( a - b , b ) ; else return gcd ( b - a , a ) ; } int main ( ) { int t ; long int a , b , gc , lcm ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%ld%ld" , & a , & b ) ; gc = gcd ( a , b ) ; lcm = ( a * b ) / gc ; printf ( "%ld %ld\n" , gc , lcm ) ; } }