int main ( ) { int t , k ; long int a , b , i , x , y , c ; scanf ( "%d" , & t ) ; for ( k = 0 ; k < t ; k ++ ) { scanf ( "%ld%ld" , & a , & b ) ; if ( a < b ) for ( i = a ; i >= 1 ; i -- ) { x = a % i ; y = b % i ; if ( x == 0 && y == 0 ) { printf ( "%ld " , i ) ; break ; } } else for ( i = b ; i >= 1 ; i -- ) { x = a % i ; y = b % i ; if ( x == 0 && y == 0 ) { printf ( "%ld " , i ) ; break ; } } c = a * b / i ; printf ( "%ld\n" , c ) ; } return 0 ; }