int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , a [ 100000 ] = { 0 } , b [ 100000 ] = { 0 } , i , j = 0 , sum = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ , j ++ ) { if ( a [ i ] < a [ i + 1 ] ) b [ j ] = a [ i ] ; else b [ j ] = a [ i + 1 ] ; } for ( i = 0 ; i < j ; i ++ ) sum += b [ i ] ; printf ( "%d\n" , sum ) ; } return 0 ; }