int main ( ) { int t , i , j , line ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d" , & line ) ; int a [ line ] [ line ] ; for ( i = 0 ; i < line ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & a [ i ] [ j ] ) ; } int sum = 0 ; for ( i = 0 ; i < line ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { if ( i == j ) sum += a [ i ] [ j ] ; } } printf ( "%d" , sum ) ; } return 0 ; }