int main ( void ) { int n , a ; scanf ( "%d" , & n ) ; while ( n -- ) { scanf ( "%d" , & a ) ; int t [ a ] [ a ] , dp [ a ] [ a ] , i , j ; for ( i = 0 ; i < a ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) scanf ( "%d" , & t [ i ] [ j ] ) ; } long int max = 0 ; for ( i = 0 ; i < a ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { if ( j == 0 ) { if ( i == 0 ) dp [ i ] [ j ] = t [ i ] [ j ] ; else dp [ i ] [ j ] = t [ i ] [ j ] + dp [ i - 1 ] [ j ] ; } else if ( j == i ) { dp [ i ] [ j ] = t [ i ] [ j ] + dp [ i - 1 ] [ j - 1 ] ; } else { if ( t [ i - 1 ] [ j - 1 ] > t [ i - 1 ] [ j ] ) dp [ i ] [ j ] = t [ i ] [ j ] + dp [ i - 1 ] [ j - 1 ] ; else dp [ i ] [ j ] = t [ i ] [ j ] + dp [ i - 1 ] [ j ] ; } if ( i == a - 1 ) { if ( dp [ i ] [ j ] > max ) max = dp [ i ] [ j ] ; } } } printf ( "%ld\n" , max ) ; } return 0 ; }