int main ( ) { int a , t ; scanf ( "%d" , & a ) ; int i , j , d [ a ] , e [ a ] , f [ a ] , g [ a ] , lcm [ a ] ; for ( i = 1 ; i <= a ; i ++ ) { scanf ( "%d%d" , & d [ i ] , & e [ i ] ) ; f [ i ] = d [ i ] ; g [ i ] = e [ i ] ; } for ( i = 1 ; i <= a ; i ++ ) { while ( e [ i ] != 0 ) { t = e [ i ] ; e [ i ] = d [ i ] % e [ i ] ; d [ i ] = t ; } lcm [ i ] = f [ i ] * g [ i ] / d [ i ] ; printf ( "%d %d\n" , d [ i ] , lcm [ i ] ) ; } return 0 ; }