int main ( void ) { int t , n , i , k , j , sum = 0 , flag = 0 , num ; scanf ( "%d" , & t ) ; while ( t -- ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( i = 1 ; i <= n ; i ++ ) { j = 1 ; k = 0 ; while ( j <= i ) { scanf ( "%d" , & num ) ; if ( num == 1 ) flag = 1 ; if ( num > k ) { k = num ; } j ++ ; } sum = sum + k ; } if ( flag = 1 ) printf ( "%d\n" , sum - 1 ) ; else printf ( "%d\n" , sum ) ; } return 0 ; }