int main ( ) { int t , n , i , count ; scanf ( "%d" , & t ) ; while ( t -- ) { count = 0 ; scanf ( "%d" , & n ) ; int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & a [ i ] ) ; } for ( i = 0 ; i < n - 1 ; i ++ ) { while ( a [ i ] < a [ i + 1 ] ) i ++ ; count ++ ; } printf ( "%d\n" , count + n ) ; } return 0 ; }