int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n , i , amount = 0 ; scanf ( "%d" , & n ) ; int arr [ n ] ; int least = arr [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; if ( arr [ i ] < least ) least = arr [ i ] ; } amount = least * ( n - 1 ) ; printf ( "%d\n" , amount ) ; } return 0 ; }