long max ( long a , long b ) { if ( a > b ) return a ; else return b ; } int main ( void ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { long a , b , g = 1 , m , i ; scanf ( "%ld %ld" , & a , & b ) ; long long l ; for ( i = 2 ; i <= a , i <= b ; i ++ ) { if ( a % i == 0 && b % i == 0 ) g = i ; } m = max ( a , b ) ; long long r = m ; while ( m % a != 0 || m % b != 0 ) m = m + r ; l = m ; printf ( "%ld %lld\n" , g , l ) ; } return 0 ; }