long long int gcd ( long long int a , long long int b ) { if ( a == 0 ) return b ; else return gcd ( b % a , a ) ; } int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long long int a , b , c = 1 , d , e ; scanf ( "%lld%lld" , & a , & b ) ; d = gcd ( b , a ) ; e = d ; while ( d != 1 ) { c = c * d ; d = gcd ( a , b ) ; a = a / d ; b = b / d ; } if ( c != 1 ) printf ( "%lld %lld\n" , e , c ) ; else if ( d == 1 ) printf ( "%lld %lld\n" , e , a * b ) ; } return 0 ; }