int main ( ) { int n , t , arr [ 100000 ] ; scanf ( "%d" , & t ) ; while ( t -- ) { int min , i ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%d" , & arr [ i ] ) ; } min = arr [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { if ( arr [ i ] < min ) min = arr [ i ] ; } printf ( "%d\n" , min * ( n - 1 ) ) ; } return 0 ; }