long long int gcd ( long long int a , long long int b ) { long long int temp ; if ( a > b ) { temp = a ; a = b ; b = temp ; } for ( ; ; ) { if ( b % a == 0 ) return a ; temp = a ; a = b % a ; b = temp ; } } int main ( ) { long long int hcf [ 1001 ] , lcm [ 1001 ] ; long long int a , b , i , t ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%lld%lld" , & a , & b ) ; hcf [ i ] = gcd ( a , b ) ; lcm [ i ] = a * b / hcf [ i ] ; } for ( i = 0 ; i < t ; i ++ ) { printf ( "%lld %lld\n" , hcf [ i ] , lcm [ i ] ) ; } }