int main ( int argc , char * * argv ) { int t , i ; scanf ( " %d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { int n ; scanf ( " %d" , & n ) ; int a [ n ] ; int j ; for ( j = 0 ; j < n ; j ++ ) { scanf ( " %d" , & a [ j ] ) ; } int x = n , k , sum = 0 ; while ( x > 1 ) { if ( a [ 0 ] > a [ 1 ] ) { sum = sum + a [ 1 ] ; for ( k = 0 ; k < n - 1 ; k ++ ) a [ k ] = a [ k + 1 ] ; } else if ( a [ 1 ] > a [ 0 ] ) { sum = sum + a [ 0 ] ; for ( k = 1 ; k < n - 1 ; k ++ ) a [ k ] = a [ k + 1 ] ; } x -- ; } printf ( "%d\n" , sum ) ; } return 0 ; }