int main ( ) { int c , t , a , b , h , i ; scanf ( "%d" , & t ) ; if ( t >= 1 && t <= 1000 ) { while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; if ( a >= 1 && a <= 1000000 && b >= 1 && b <= 1000000 ) { if ( a == b ) { h = a ; } else { c = a < b ? a : b ; for ( i = c ; i > 0 ; i -- ) { if ( a % i == 0 && b % i == 0 ) { h = i ; break ; } } } printf ( "%d %d\n" , h , ( a * b ) / h ) ; } } } return 0 ; }