int gcd ( int a , int b ) { int x ; if ( a == 0 ) return b ; else if ( b == 0 ) return a ; else { while ( b != 0 ) { x = a % b ; a = b ; b = x ; } return a ; } } int lcm ( int x , int y , int z ) { int g = y / x ; return z * g ; } int main ( ) { int t , a , b , g , l ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d %d" , & a , & b ) ; g = gcd ( a , b ) ; l = lcm ( g , a , b ) ; printf ( "%d %d\n" , g , l ) ; } return 0 ; }