int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int a , b ; scanf ( "%d%d" , & a , & b ) ; long int gcd = ( a < b ) ? a : b ; long int lcm = ( a > b ) ? a : b ; for ( long int i = gcd ; i > 0 ; i -- ) { if ( ( a % i == 0 ) && ( b % i == 0 ) ) { gcd = i ; break ; } } for ( long int i = lcm ; i < 1000000000 ; i ++ ) { if ( ( i % a == 0 ) && ( i % b == 0 ) ) { lcm = i ; break ; } } printf ( "%lld %lld\n" , gcd , lcm ) ; } return 0 ; }