long int gcd ( long int a , long int b ) ; int main ( ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { long int a , b ; scanf ( "%ld %ld" , & a , & b ) ; long int i , x ; i = gcd ( a , b ) ; x = ( a * b ) / i ; printf ( "%ld %ld\n" , i , x ) ; } } long int gcd ( long int a , long int b ) { if ( a == 0 || b == 0 ) return 0 ; else if ( a == b ) return a ; else if ( a > b ) return gcd ( a - b , b ) ; else return gcd ( a , b - a ) ; }