int main ( ) { int a , b , t , i , c , gcd , lcm , j = 1 , p , q , x ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; { if ( a > b ) x = a ; else x = b ; for ( ; x >= 1 ; x -- ) { if ( a % x == 0 && b % x == 0 ) { gcd = x ; break ; } } } { do { p = a * j ; q = b * j ; if ( p = q ) { lcm = p ; break ; } } while ( j ++ ) ; } printf ( "%d %d" , gcd , lcm ) ; } return 0 ; }