int main ( ) { int T ; scanf ( "%d" , & T ) ; while ( T -- ) { unsigned int a , i , s , b , h , l ; scanf ( "%u %u" , & a , & b ) ; if ( a <= b ) { s = a ; } else s = b ; for ( i = 1 ; i <= s ; i ++ ) { if ( a % i == 0 && b % i == 0 ) h = i ; } l = ( a * b ) / h ; printf ( "%u %u/n" , h , l ) ; } return 0 ; }