int gcd ( int , int ) ; int main ( ) { int t , a , b , g , l ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { scanf ( "%d %d" , & a , & b ) ; g = gcd ( a , b ) ; l = ( a * b ) / g ; printf ( "\n%d %d" , g , l ) ; } return 0 ; } int gcd ( int x , int y ) { if ( x == 0 || y == 0 ) return 0 ; if ( x > y ) return gcd ( x - y , y ) ; if ( x < y ) return gcd ( x , y - x ) ; if ( x == y ) return x ; }