int main ( ) { int n , i , j ; scanf ( "%d" , & n ) ; int a [ n ] [ n ] ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { scanf ( "%d" , & a [ i ] [ j ] ) ; } } for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j <= i ; j ++ ) { printf ( "%d" , a [ i ] [ j ] ) ; } printf ( "\n" ) ; } int max = a [ 0 ] [ 0 ] ; for ( i = 0 ; i < n ; i ++ ) { int sum = 0 ; for ( j = 0 ; j <= i ; j ++ ) { sum = sum + a [ i ] [ j ] ; } if ( max < sum ) ; { max = sum ; } } printf ( "\n%d" , max ) ; return 0 ; }