int main ( ) { int t , i , n , sum , a [ 50000 ] ; scanf ( "%d" , & t ) ; while ( t -- ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = n - 1 ; i > 0 ; i -- ) { if ( a [ i - 1 ] > a [ i ] ) { a [ i - 1 ] = a [ i ] ; sum += a [ i ] ; a [ i ] = 0 ; } else { sum += a [ i - 1 ] ; a [ i ] = 0 ; } } printf ( "%d\n" , sum ) ; } return 0 ; }