int main ( ) { int i , j , n , t , temp = 0 ; scanf ( "%d" , & t ) ; int b [ t ] ; for ( i = 0 ; i < t ; i ++ ) { scanf ( "%d" , & n ) ; int a [ n ] ; temp = 0 ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%d" , & a [ j ] ) ; for ( j = 0 ; j < n - 1 ; j ++ ) { if ( a [ j ] > a [ j + 1 ] ) temp += a [ j + 1 ] ; else temp += a [ j ] ; } b [ i ] = temp ; } for ( i = 0 ; i < t ; i ++ ) printf ( "\n %d" , b [ i ] ) ; return ( 0 ) ; }