int main ( void ) { int T , n , i , j , B [ 2 ] , sum ; long long m ; scanf ( "%d" , & T ) ; while ( T -- ) { sum = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { for ( j = 0 ; j < i ; j ++ ) { scanf ( "%lld" , & m ) ; if ( ( j == 0 ) && ( i != n - 1 ) ) sum += m ; else if ( ( i == n - 1 ) && ( j == 0 ) ) B [ 0 ] = m ; else if ( ( i == n - 1 ) && ( j == 1 ) ) B [ 1 ] = m ; } } if ( B [ 0 ] > B [ 1 ] ) sum += B [ 0 ] ; else sum += B [ 1 ] ; printf ( "%lld\n" , sum ) ; } return 0 ; }