int main ( ) { unsigned int a , b , i , t , n , j , e ; scanf ( "%u" , & n ) ; for ( i = 0 ; i < n ; i ++ ) { scanf ( "%u%u" , & a , & b ) ; if ( b < a ) { t = b ; b = a ; a = t ; } for ( j = 1 ; j <= a ; j ++ ) { if ( a % j == 0 && b % j == 0 ) { e = j ; } } printf ( "%u %u\n" , e , a * b / e ) ; } }