int main ( ) { int t , b , a , i , s , g , l , j ; scanf ( "%d" , & t ) ; for ( i = 1 ; i <= t ; i ++ ) { scanf ( "%d" , & a ) ; scanf ( "%d" , & b ) ; if ( a < b ) s = a ; else s = b ; for ( j = 1 ; j <= s ; j ++ ) { if ( a % i == 0 && b % i == 0 ) g = i ; } for ( j = s ; j < ( a * b ) ; j += s ) { if ( i % a == 0 && i % b == 0 ) { l = i ; break ; } } printf ( "%d %d\n" , g , l ) ; } return 0 ; }