int main ( ) { int i , t , count , n , sub ; scanf ( "%d" , & t ) ; while ( t -- ) { count = 0 , sub = 0 ; scanf ( "%ld" , & n ) ; long int a [ n ] ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%ld" , & a [ i ] ) ; for ( i = 0 ; i < n - 1 ; i ++ ) { if ( a [ i ] < a [ i + 1 ] ) count ++ ; else { sub += ( count * ( count + 1 ) / 2 ) ; count = 0 ; } } printf ( "%d\n" , sub + n ) ; } return 0 ; }