void main ( ) { int n ; scanf ( "%d" , & n ) ; long long int gcd [ n ] , lcm [ n ] , i , j ; for ( i = 0 ; i < n ; i ++ ) { long long int num1 = 0 , num2 = 0 , remainder = 0 , numerator = 0 , denominator = 0 ; scanf ( "%d %d" , & num1 , & num2 ) ; if ( num1 > num2 ) { numerator = num1 ; denominator = num2 ; } else { numerator = num2 ; denominator = num1 ; } remainder = numerator % denominator ; while ( remainder != 0 ) { numerator = denominator ; denominator = remainder ; remainder = numerator % denominator ; } gcd [ i ] = denominator ; lcm [ i ] = num1 * num2 / gcd [ i ] ; } printf ( "\n" ) ; for ( i = 0 ; i < n ; i ++ ) { printf ( "%lld %lld" , gcd [ i ] , lcm [ i ] ) ; printf ( "\n" ) ; } }