int main ( ) { int t ; scanf ( "%d" , & t ) ; for ( int i = 0 ; i < t ; i ++ ) { int n , tot_n ; scanf ( "%d" , & n ) ; tot_n = n * ( n + 1 ) / 2 ; int tri [ tot_n ] ; for ( int j = 0 ; j < tot_n ; j ++ ) { scanf ( "%d" , & tri [ j ] ) ; } for ( int j = n - 1 ; j > 0 ; j -- ) { int c = j * ( j - 1 ) / 2 ; for ( int k = c ; k < c + j ; k ++ ) { if ( tri [ k + j ] > tri [ k + j + 1 ] ) tri [ k ] += tri [ k + j ] ; else tri [ k ] += tri [ k + j + 1 ] ; } } printf ( "%d\n" , tri [ 0 ] ) ; } return 0 ; }