int main ( void ) { int t ; scanf ( "%d" , & t ) ; while ( t -- ) { int n ; scanf ( "%d" , & n ) ; int arr [ n ] [ n ] ; int x = 1 , y ; scanf ( "%d" , & arr [ 0 ] [ 0 ] ) ; int max = - 1 ; while ( x < n ) { for ( int i = 0 ; i <= x ; i ++ ) { scanf ( "%d" , & y ) ; if ( i == 0 ) { arr [ x ] [ 0 ] = y + arr [ x - 1 ] [ 0 ] ; } else if ( i == x ) { arr [ x ] [ x ] = y + arr [ x - 1 ] [ x - 1 ] ; } else { int p = arr [ x - 1 ] [ i - 1 ] + y ; int q = arr [ x - 1 ] [ i ] + y ; if ( p > q ) { arr [ x ] [ i ] = p ; } else { arr [ x ] [ i ] = q ; } } if ( x == n - 1 ) { if ( arr [ x ] [ i ] > max ) { max = arr [ x ] [ i ] ; } } } x ++ ; } printf ( "%d\n" , max ) ; } return 0 ; }