int main ( void ) { int iTestCases , iTotalElts , iMin , iElement , iSum ; fscanf ( stdin , "%d" , & iTestCases ) ; for ( ; iTestCases > 0 ; iTestCases -- ) { iSum = 0 ; fscanf ( stdin , "%d" , & iTotalElts ) ; iSum = iTotalElts - 1 ; if ( iTotalElts > 0 ) { fscanf ( stdin , "%d" , & iElement ) ; iMin = iElement ; iTotalElts -- ; } for ( ; iTotalElts > 0 ; iTotalElts -- ) { fscanf ( stdin , "%d" , & iElement ) ; ( iMin > iElement ) ? ( iMin = iElement ) : ( iMin = iMin ) ; } fprintf ( stdout , "%d\n" , iSum * iMin ) ; } return 0 ; }