long gcd ( long a , long b ) ; int main ( void ) { int t ; long a , b ; unsigned long long g , l , i ; scanf ( "%d" , & t ) ; while ( t -- ) { i = 1 ; scanf ( "%ld%ld" , & a , & b ) ; g = gcd ( a , b ) ; l = ( a * b ) / g ; printf ( "%llu %llu\n" , g , l ) ; } return 0 ; } long gcd ( long a , long b ) { if ( a == 0 || b == 0 ) return 0 ; if ( a == b ) return a ; if ( a > b ) return gcd ( a - b , b ) ; return gcd ( a , b - a ) ; }