int main ( ) { int test ; scanf ( "%d" , & test ) ; while ( test -- ) { int a , b , lcm , hcf ; scanf ( "%d%d" , & a , & b ) ; int x = a , y = b ; if ( y > x ) { while ( x != 0 ) { int z = y % x ; y = x ; hcf = x ; x = z ; } } else { while ( y != 0 ) { int z = x % y ; x = y ; hcf = y ; y = z ; } } if ( a > b ) { if ( a % b == 0 ) lcm = a ; else { int i = 2 ; int m = a ; while ( a % b != 0 ) { a = m * i ; i ++ ; lcm = a ; } } } else { if ( b % a == 0 ) lcm = b ; else { int i = 2 , m = b ; while ( b % a != 0 ) { b = m * i ; i ++ ; lcm = b ; } } } printf ( "%d %d\n" , hcf , lcm ) ; } return 0 ; }