int main ( void ) { int t , a , b , c ; scanf ( "%d" , & t ) ; while ( t -- ) { scanf ( "%d%d" , & a , & b ) ; if ( a > b ) { c = 1 ; LOOP : for ( int i = b ; i > 1 ; i -- ) { if ( b % i == 0 && a % i == 0 ) { b = b / i ; a = a / i ; c = c * i ; goto LOOP ; } } } if ( a < b ) { c = 1 ; LOOP1 : for ( int i = a ; i > 1 ; i -- ) { if ( b % i == 0 && a % i == 0 ) { b = b / i ; a = a / i ; c = c * i ; goto LOOP1 ; } } } if ( a == b ) { printf ( "%d %d\n" , a ) ; break ; } printf ( "%d %d\n" , c , a * b / c ) ; } }