int main ( ) { unsigned long int n , cost ; int t , i , j ; scanf ( "%d" , & t ) ; for ( i = 0 ; i < t ; i ++ ) { cost = 0 ; scanf ( "%ld" , & n ) ; unsigned long int a [ n ] ; for ( j = 0 ; j < n ; j ++ ) scanf ( "%ld" , & a [ j ] ) ; for ( j = 0 ; j < n - 1 ; j ++ ) { if ( a [ j ] > a [ j + 1 ] ) cost = a [ j + 1 ] + cost ; else cost = a [ j ] + cost ; } printf ( "%ld\n" , cost ) ; } return 0 ; }