int main ( ) { int T , t , a [ 100 ] , n , i , sum [ 10 ] ; scanf ( "%d" , & t ) ; T = t ; while ( t > 0 ) { sum [ t ] = 0 ; scanf ( "%d" , & n ) ; for ( i = 0 ; i < n ; i ++ ) scanf ( "%d" , & a [ i ] ) ; for ( i = 0 ; i < n ; i ++ ) { if ( a [ i ] > a [ i + 1 ] ) sum [ t ] = sum [ t ] + a [ i + 1 ] ; else sum [ t ] = sum [ t ] + a [ i ] ; } t -- ; } for ( t = T ; t > 0 ; t -- ) printf ( "%d\n" , sum [ t ] ) ; return 0 ; }